const Iron = context.global.iron;

var t = msg.i18n;

// node.log("Enter chatId: " + msg.payload.chatId);

// https://www.npmjs.com/package/geolib
var dist = context.global.geolib.getDistance(
    { latitude: msg.payload.location.lat, 
      longitude: msg.payload.location.lon
    },
    { latitude: msg.lat, 
      longitude: msg.lon
    }
); 

