// node.log("userId: " + msg.user._id + " type " + msg.payload.type + " maxDist " + maxDist + " dist " + dist/1000);

if (dist/1000 <= maxDist) {
    var km = Math.round(dist/1000 * 10) / 10;
    var content;
    var when = msg.originalPayload.when;

    if (msg.type === "vecinal") {
        // kmvecinal : 'üî• Alerta de posible fuego a {{km}} –∫–º de distancia (fuente NASA)''
        content = t('kmvecinal', {km: km});
    } else if (msg.type === "modis" || msg.type === "viirs") {
        // kmnasa : 'üî• Alerta vecinal de fuego a {{km}} –∫–º de distancia''
        content = t('kmnasa', {km: km});
    }

    var password = global.get('ironPassword');

    delete msg.originalPayload._id;

    msg.originalPayload.ourversion = "1";

    Iron.seal(msg.originalPayload, password, Iron.defaults).then(function(sealed) {
        const link = t('firelink', { server: global.get('website'), enc: sealed, escapeInterpolation: false });
        var caption = {
            payload : {
              chatId : msg.payload.chatId,
              type : "message",
              content: content,
              options: { parse_mode: "Markdown" }
            },
            link,
            sealed,
            i18n: t,
            lang: msg.lang
        };

        var notification = {
            // find if we send previously this notification
            payload : {
              chatId : msg.payload.chatId,
              type : "location",
              content: {
                 longitude: msg.lon,
                 latitude: msg.lat 
              }
            },
            // FIXME this shoudld be removed?
            // caption: caption,
            i18n: t,
            lang: msg.lang
        };

        // console.log(when);

        var newMsg = {
            payload: {
                userId: msg.user._id,
                // chatId: msg.payload.chatId,
                geo: { $near : {
                    $geometry: { 
                        type: "Point",  
                        coordinates: [
                            msg.lon,
                            msg.lat
                        ] },
                    $minDistance: 0,
                    $maxDistance: 500 // Junto notificaciones en un radio de 0.5km 
                    }
                }
            },
            notification: notification,
            caption: caption,
            cacheKey: msg.lat + "," + msg.lon,
            i18n: t,
            user: msg.user,
            notitype: msg.payload.type,
            when: when,
            originalPayload: msg.originalPayload,
            lang: msg.lang
        };
        newMsg.collection = "notifications";
        node.send(newMsg);

    }, function(reason) {
        node.error(reason);
    });





