    var lon = pay.longitude;
    if (typeof lat === 'number' && typeof lon === 'number')  {
        // Sometimes the data is not well formed/downloaded
        var ourid = {
            type: "Point",
            coordinates: [ lon, lat ]
        };

        // dates sample
        // acq_date: "2017-11-09"
        ///acq_time: "06:30"
        var acdate = pay.acq_date;
        var actime = pay.acq_time;
        var when = context.global.moment.utc(acdate + " " + actime, "YYYY-MM-DD HH:mm").toDate(); // .valueOf();

        var setcommon = {
            ourid: ourid,
            lat: lat,
            lon: lon,
            updatedAt: now,
            type: type,
            when : when,
            acq_date: acdate,
            acq_time: actime,
            scan : pay.scan,
            track : pay.track,
            satellite : pay.satellite,
            confidence : pay.confidence,
            version : pay.version,
            frp : pay.frp,
            daynight : pay.daynight,
        };
        var setdiff;
        if (type === "modis") {
            setdiff = {
                brightness : pay.brightness,
                bright_t31 : pay.bright_t31
            };
        } else { // viirs
            setdiff = {
                bright_ti4 : pay.bright_ti4,
                bright_ti5 : pay.bright_ti5,
            };
            }

        var setjoin = Object.assign(setcommon, setdiff);

        // remove empties
        /* Object.keys(setjoin).forEach((key) => (setjoin[key] === null || typeof setjoin[key] === 'undefined') && 
               delete setjoin[key]); */

        var set = { 
            $set: setjoin,
            $setOnInsert: { createdAt: now }
        };

        /* mongo basic
        var update = {};
        update.payload = set;
        update.query = { _id:  id };
        updates.push(update);
        */

        // http://mongodb.github.io/node-mongodb-native/2.1/api/Collection.html#bulkWrite
        // { updateOne: { filter: {a:2}, update: {$set: {a:2}}, upsert:true } }
        updates.push({ updateOne: { filter: {ourid: ourid, type: type}, update: set, upsert:true } });
    }
}

if (updates.length > 0 ) {
    // If we have updates
    var bulkUpdate = {};
    bulkUpdate.collection = collection;
    // http://mongodb.github.io/node-mongodb-native/2.1/api/Collection.html#bulkWrite
    // https://docs.mongodb.com/manual/reference/write-concern/
    bulkUpdate.payload = [updates, {w: 1, wtimeout: 60000}];
    return bulkUpdate;
}

