function isNumeric(n) {
  return !isNaN(parseFloat(n)) && isFinite(n);
}

function getDirection(degree){
    if (degree>337.5) return t("norte") + " ‚¨Ü";
    if (degree>292.5) return t("noroeste") + " ‚Üñ";
    if(degree>247.5) return t("oeste") + " ‚¨Ö";
    if(degree>202.5) return t("suroeste") + " ‚Üô";
    if(degree>157.5) return t("sur") + " ‚¨á";
    if(degree>122.5) return t("sureste") + " ‚Üò"; 
    if(degree>67.5) return t("este") + " ‚û°"; 
    if(degree>22.5){return t("noreste") + " ‚Üó";}
    return t("norte") + " ‚¨Ü";
}

// msg.caption, msg.notification]
var onWeather = function (err, w, msg) {
    /* wind.speed Wind speed. Unit Default: meter/sec, Metric: meter/sec, Imperial: miles/hour.
     wind.deg Wind direction, degrees (meteorological)*/

    if (err || !w.wind || !isNumeric(w.wind.deg) || !isNumeric(w.wind.speed)) {
        // send without weather info
        // console.log("weather : " + JSON.stringify(w) + ", err : " + JSON.stringify(err));
        msg.caption.payload.content += ":";
        // node.send(msg.caption);
        // node.send(msg.notification);
        node.send(msg);
    } else {
        var dir = getDirection(w.wind.deg);
        // sometimes gust (rafagas)
        // lang: es, so we use translation from Spanish later
        var type = beaufortScale(w.wind.speed, {lang: 'es', int: true});
        if (type && dir) {
            if (type.grade > 0) {
                // "(direcci√≥n del viento {{dir}} de tipo '{{desc}}')",

                var windinfo = t("wind", {dir: dir, desc: t(type.desc).toLowerCase()});
                msg.caption.payload.content += ", " + windinfo + ":";
            } else {
                msg.caption.payload.content += ", " + t("sin viento") + ":";
            }
        } else {
            msg.caption.payload.content += ":";
        }
        node.send(msg);
        // node.send(msg.caption);
        // node.send(msg.notification);
    }
};

