    var path = global.get('ftp-path');
    var updates = [];
    for (var i = 0; i < msg.payload.length; i++) {
        var fire = msg.payload[i];
        var acdate = fire.acq_date;
        var actime = fire.acq_time;
        var when = context.global.moment.utc(acdate + " " + actime, "YYYY-MM-DD HH:mm").toDate(); // .valueOf();
        fire.when = when;

        var ourid = {
            type: fire.type,
            when: when,
            fire: tracked.name
        };

        var setcommon = {
            //_id: id,
            ourid: ourid,
            when : when,
            longitude: fire.longitude,
            latitude: fire.latitude,
            acq_date: acdate,
            acq_time: actime,
            scan : fire.scan,
            track : fire.track,
            satellite : fire.satellite,
            confidence : fire.confidence,
            version : fire.version,
            frp : fire.frp,
            daynight : fire.daynight,
        };
        var setdiff;
        if (fire.type === "modis") {
            setdiff = {
                brightness : fire.brightness,
                bright_t31 : fire.bright_t31
            };
        } else { // viirs
            setdiff = {
                bright_ti4 : fire.bright_ti4,
                bright_ti5 : fire.bright_ti5,
            };
        }

        var setjoin = Object.assign(setcommon, setdiff);

        var set = { 
            $set: setjoin
        };

        // http://mongodb.github.io/node-mongodb-native/2.1/api/Collection.html#bulkWrite
        // { updateOne: { filter: {a:2}, update: {$set: {a:2}}, upsert:true } }
        updates.push({ updateOne: { filter: {ourid: ourid}, update: set, upsert:true } });

        var email = {
            fire: tracked.name,
            date: acdate,
            time: actime
        }
        node.send([null, email]);
    }
    var bulkUpdate = {};
    bulkUpdate.collection = "trackedfireshistory";
    // http://mongodb.github.io/node-mongodb-native/2.1/api/Collection.html#bulkWrite
    // https://docs.mongodb.com/manual/reference/write-concern/
    bulkUpdate.payload = [updates, {w: 1, wtimeout: 60000}];
    return bulkUpdate;
}

